        public static ExcercisesLib LoadFromFile(string fn)
        {
            var xs = new XmlSerializer(typeof (ExcercisesLib));
            var fs = new FileStream(fn, FileMode.Open, FileAccess.Read);
            var toReturn = (ExcercisesLib) xs.Deserialize(fs);
            fs.Close();
            return toReturn;
        }

        public void SaveToFile(string fn)
        {
            var xs = new XmlSerializer(typeof (ExcercisesLib));
            var fs = new FileStream(fn, FileMode.Create, FileAccess.Write);
            xs.Serialize(fs, this);
            fs.Close();
        }
    }
